let decision_lock = false;

frappe.ui.form.on('Evaluation Periode Essai employe', {

    // ==============================
    // AUTO-REMPLISSAGE DES CRITÈRES
    // ==============================
    onload(frm) {
        if (!frm.is_new()) return;

        frm.clear_table('evaluation_details');

        const criteres = [
            "حجم العمل | Volume de travail",
            "نوعية العمل | Qualité du travail",
            "تنفيذ المهام المرتبطة بالمنصب | Application des tâches liées au poste",
            "الاجتهاد والتفاني في العمل | Abnégation et dévouement au travail",
            "المحافظة على وسائل العمل | Entretien et soin des moyens de travail",
            "احترام تعليمات المسؤول المباشر | Respect des consignes de la hiérarchie",
            "القدرة على أداء مهام متعددة | Polyvalence",
            "المواظبة والانضباط العام | Assiduité et discipline générale",
            "روح الفريق | Esprit d’équipe",
            "روح المبادرة | Esprit d’initiative",
            "القدرة البدنية والتحمل | Robustesse",
            "استخدام الأدوات المعلوماتية | Outil informatique",
            "الجاهزية والتوفر | Disponibilité"
        ];

        criteres.forEach(c => {
            let row = frm.add_child('evaluation_details');
            row.criteria = c;
        });

        frm.refresh_field('evaluation_details');
    },

    // ==============================
    // BLOQUER AJOUT / SUPPRESSION
    // ==============================
    refresh(frm) {
        if (frm.fields_dict.evaluation_details) {
            const grid = frm.fields_dict.evaluation_details.grid;
            grid.cannot_add_rows = true;
            grid.cannot_delete_rows = true;
            grid.toggle_enable('criteria', false);
        }
    },

    // ==============================
    // DÉCISION FINALE (SANS BOUCLE)
    // ==============================
    decision_success(frm) {
        handle_decision(frm, 'decision_success');
    },
    decision_failed(frm) {
        handle_decision(frm, 'decision_failed');
    },
    decision_extend(frm) {
        handle_decision(frm, 'decision_extend');
    }
});

// ==========================================
// CHILD DOCTYPE : Evaluation Periode Essai Detail
// ==========================================
frappe.ui.form.on('Evaluation Periode Essai Detail', {

    score_1(frm, cdt, cdn) { set_score(frm, cdt, cdn, 1); },
    score_2(frm, cdt, cdn) { set_score(frm, cdt, cdn, 2); },
    score_3(frm, cdt, cdn) { set_score(frm, cdt, cdn, 3); },
    score_4(frm, cdt, cdn) { set_score(frm, cdt, cdn, 4); },
    score_5(frm, cdt, cdn) { set_score(frm, cdt, cdn, 5); }

});

// ==============================
// FONCTION : SET SCORE
// ==============================
function set_score(frm, cdt, cdn, value) {
    let row = locals[cdt][cdn];

    for (let i = 1; i <= 5; i++) row[`score_${i}`] = 0;

    row[`score_${value}`] = 1;
    row.final_score = value;

    frm.refresh_field('evaluation_details');
    calculate_average(frm);
}

// ==============================
// FONCTION : CALCUL MOYENNE
// ==============================
function calculate_average(frm) {
    let total = 0;
    let count = 0;

    (frm.doc.evaluation_details || []).forEach(r => {
        if (r.final_score) {
            total += r.final_score;
            count++;
        }
    });

    frm.set_value('average_score', count ? (total / count).toFixed(2) : 0);
}

// ==============================
// FONCTION : DÉCISION UNIQUE (SAFE)
// ==============================
function handle_decision(frm, selected) {
    if (decision_lock) return;
    decision_lock = true;

    ['decision_success', 'decision_failed', 'decision_extend'].forEach(f => {
        frm.doc[f] = (f === selected) ? 1 : 0;
    });

    frm.refresh_fields([
        'decision_success',
        'decision_failed',
        'decision_extend'
    ]);

    decision_lock = false;
}

frappe.ui.form.on('Supplier Evaluation', {
    onload_post_render: function(frm) {
        // Charger automatiquement les critères si le tableau est vide
        if (!frm.doc.criteria_table || frm.doc.criteria_table.length === 0 || 
            (frm.doc.criteria_table.length === 1 && !frm.doc.criteria_table[0].criteria)) {
            console.log("Chargement des critères par défaut...");
            load_default_criteria(frm);
        }
    },

    refresh: function(frm) {
        // Colorer le classement si total_score existe
        if (frm.doc.total_score) {
            set_color_indicator(frm);
        }
    },

    before_save: function(frm) {
        // Vérifier que la somme des pondérations = 100
        let total_weighting = 0;
        if (frm.doc.criteria_table) {
            frm.doc.criteria_table.forEach(row => {
                if (row.weighting) total_weighting += parseFloat(row.weighting);
            });
        }
       
        // Forcer le chargement des critères si vide
        if (!frm.doc.criteria_table || frm.doc.criteria_table.length === 0) {
            load_default_criteria(frm);
        }
    }
});

// Gestion des champs du child table
frappe.ui.form.on('Supplier Evaluation Criteria', {
    weighting: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];

        // Forcer pondération entre 0 et 100
        if (row.weighting < 0) row.weighting = 0;
        if (row.weighting > 100) row.weighting = 100;
        frappe.model.set_value(cdt, cdn, 'weighting', row.weighting);

        // Calculer la somme totale des pondérations
        let total_weighting = 0;
        if (frm.doc.criteria_table) {
            frm.doc.criteria_table.forEach(r => {
                if (r.weighting) total_weighting += parseFloat(r.weighting);
            });
        }

        // Afficher l'alerte uniquement si dépassement
        if (total_weighting > 100) {
            frappe.show_alert({
                message: __("Attention : la somme des pondérations dépasse 100 ! (Actuel: {0})", [total_weighting]),
                indicator: 'red'
            });
        }
        
        // Recalculer le score pondéré et total
        calculate_weighted_score(frm, cdt, cdn);
        calculate_total(frm);
    },
    
    score: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        
        // Forcer score entre 0 et 5
        if (row.score < 0) row.score = 0;
        if (row.score > 5) row.score = 5;
        frappe.model.set_value(cdt, cdn, 'score', row.score);
        
        // Recalculer le score pondéré et total
        calculate_weighted_score(frm, cdt, cdn);
        calculate_total(frm);
    }
});

// Calcul du score pondéré pour chaque ligne
function calculate_weighted_score(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    if (row.score !== undefined && row.weighting !== undefined && row.score > 0 && row.weighting > 0) {
        let weighted = ((row.score / 5) * row.weighting);
        frappe.model.set_value(cdt, cdn, 'weighted_score', parseFloat(weighted.toFixed(2)));
    } else {
        frappe.model.set_value(cdt, cdn, 'weighted_score', 0);
    }
}

// Calcul du total et détermination du classement
function calculate_total(frm) {
    let total = 0;
    let total_weighting = 0;

    if (frm.doc.criteria_table) {
        frm.doc.criteria_table.forEach(function(row) {
            if (row.weighted_score) total += parseFloat(row.weighted_score);
            if (row.weighting) total_weighting += parseFloat(row.weighting);
        });
    }

    frm.set_value('total_score', parseFloat(total.toFixed(2)));

    // Déterminer le classement
    if (total >= 85) {
        frm.set_value('classement', 'Fournisseur excellent');
        frm.set_value('decision', 'Maintenir et developper');
    } else if (total >= 70) {
        frm.set_value('classement', 'Fournisseur approuvé');
        frm.set_value('decision', 'Suivi normal');
    } else if (total >= 60) {
        frm.set_value('classement', 'Sous surveillance');
        frm.set_value('decision', 'Actions correctives exigées ');
    } else {
        frm.set_value('classement', 'Non approuvé');
        frm.set_value('decision', 'Retirer de la liste ou plan ');
    }

    set_color_indicator(frm);
}

// Chargement des critères par défaut
function load_default_criteria(frm) {
    const criteria_list = [
        "Qualité du produit ou service / conformité",
        "Respect des délais / transit",
        "Prix et conditions commerciales",
        "Réactivité et communication",
        "Fiabilité documentaire (douane, BL, certificats)"
    ];

    console.log("Ajout de " + criteria_list.length + " critères...");

    frm.clear_table('criteria_table');

    criteria_list.forEach(function(criteria_name) {
        let row = frm.add_child('criteria_table');
        row.criteria = criteria_name;
        row.weighting = 0;
        row.score = 0;
        row.weighted_score = 0;
    });

    frm.refresh_field('criteria_table');
    console.log("Critères chargés ! Total: " + frm.doc.criteria_table.length);
}

// Coloration du classement
function set_color_indicator(frm) {
    let score = frm.doc.total_score || 0;
    let color;

    if (score >= 85) color = 'green';
    else if (score >= 70) color = 'blue';
    else if (score >= 60) color = 'orange';
    else color = 'red';

    setTimeout(function() {
        if (frm.fields_dict.classement) {
            frm.get_field('classement').$wrapper.find('.control-value').css({
                'color': color,
                'font-weight': 'bold',
                'font-size': '14px'
            });
        }

        if (frm.fields_dict.total_score) {
            frm.get_field('total_score').$wrapper.find('.control-value').css({
                'color': color,
                'font-weight': 'bold',
                'font-size': '16px'
            });
        }
    }, 100);
}

