function calculate_average(frm) {
    let total = 0;
    let count = 0;
    (frm.doc.evaluation_details || []).forEach(r => {
        if (r.final_score) {
            total += r.final_score;
            count++;
        }
    });

    let average = count ? (total / count).toFixed(2) : 0;
    let score100 = Math.round((average / 5) * 100);
    
    frm.set_value('average_score', average);
    frm.set_value('final_score_100', score100);

    // Détermination de la mention (Barème Image 3)
    let mention = "";
    if (score100 >= 86) {
        mention = "Excellent";
    } else if (score100 >= 66) {
        mention = "Satisfaisant";
    } else if (score100 >= 51) {
        mention = "Acceptable";
    } else if (score100 >= 26) {
        mention = "Faible";
    } else {
        mention = "Insuffisant";
    }

    // Mise à jour sécurisée du champ mention
    if (frm.fields_dict.final_appreciation) {
        frm.set_value('final_appreciation', mention);
    }
}

